changeSet.sql:
## MySQL data conversion for CA Account ICR values
## Set the acct seq to current max of ICR accounts - default to 10000 set @acct_seq = IFNULL((select max(id) from CA_ICR_ACCT_SEQ), 10000); select @acct_seq;
## CA_ICR_ACCT_T INSERT CA_ICR_ACCT_T (CA_ICR_ACCT_GNRTD_ID, OBJ_ID, VER_NBR, FIN_COA_CD, ACCOUNT_NBR, ICR_FIN_COA_CD, ICR_FIN_ACCT_NBR, ACLN_PCT, DOBJ_MAINT_CD_ACTV_IND) SELECT @acct_seq := @acct_seq + 1, uuid(), 1, FIN_COA_CD, ACCOUNT_NBR, ICR_FIN_COA_CD, ICR_ACCOUNT_NBR, 100, 'Y' FROM CA_ACCOUNT_T WHERE ICR_ACCOUNT_NBR IS NOT NULL AND ICR_FIN_COA_CD IS NOT NULL ;
#Data conversion completed, increase the current max on the sequence table INSERT CA_ICR_ACCT_SEQ value (@acct_seq);
## -------------------------------------------------------------------- ##
## Set the acct seq to current max of A21 ICR accounts - default to 10000 set @a21_acct_seq = IFNULL((select max(id) from CA_A21_ICR_ACCT_SEQ), 10000); select @a21_acct_seq;
INSERT INTO CA_A21_ICR_ACCT_T (CA_A21_ICR_ACCT_GNRTD_ID, OBJ_ID, VER_NBR, FIN_COA_CD, ACCOUNT_NBR, SUB_ACCT_NBR, ICR_FIN_COA_CD, ICR_FIN_ACCT_NBR, ACLN_PCT, DOBJ_MAINT_CD_ACTV_IND) SELECT @a21_acct_seq := @a21_acct_seq + 1, uuid(), 1, FIN_COA_CD, ACCOUNT_NBR, SUB_ACCT_NBR, ICR_FIN_COA_CD, ICR_ACCOUNT_NBR, 100, 'Y' FROM CA_A21_SUB_ACCT_T WHERE ICR_ACCOUNT_NBR IS NOT NULL AND ICR_FIN_COA_CD IS NOT NULL ;
#Data conversion completed, increase the current max on the sequence table INSERT CA_A21_ICR_ACCT_SEQ value (@a21_acct_seq);
